function Main() {


  function handleEditAvatarClick() {
    const popup = document.querySelector('.popup_type_edit-profile-image');
    popup.classList.add('popup_visible');
  }

  function handleEditProfileClick() {
    const popup = document.querySelector('.popup_type_edit-profile');
    popup.classList.add('popup_visible');
  }

  function handleAddPlaceClick() {
    const popup = document.querySelector('.popup_type_add-card');
    popup.classList.add('popup_visible');
  }

  return (
    <main className="content">
    <section className="profile">
      <div className="profile__image-container">
        <img id="profile-image" src='#' alt="No avatar" className="profile__image"/>
        <button className="profile__image-overlay" onClick={handleEditAvatarClick}></button>
      </div>
      <div className="profile__container profile__overflow-element">
        <h1 className="profile__name profile__overflow-element"></h1>
        <button type="button" className="profile__button profile__button_type_edit" onClick={handleEditProfileClick} aria-label="Edit profile"></button>
      </div>
      <p className="profile__info profile__overflow-element"></p>
      <button type="button" className="profile__button profile__button_type_add-image" onClick={handleAddPlaceClick} aria-label="Add image"></button>
    </section>
    <section className="images-container">
    </section>
    </main>
  );
}

export default Main;
