import React from 'react';
import api from '../utils/api.js';


function Main(props) {
  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');
  const [userAvatar , setUserAvatar] = React.useState('');
  const [cards, setCards] = React.useState([]);
  
  React.useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
.then(([userInfo, cards]) => {
  userData.setUserInfo(userInfo.name, userInfo.about, userInfo.avatar, userInfo._id);
  cardList.renderer(cards);
})
.catch((error) => {
  console.log(error);
});
    api.getUserInfo().
    then(res => {
      setUserName(res.name);
      setUserDescription(res.about);
      setUserAvatar(res.avatar);
    })
    .catch(err => {
      console.log(err);
    })
  },[]);


  return (
    <main className="content">
    <section className="profile">
      <div className="profile__image-container">
        <img id="profile-image" src={userAvatar} alt={`${userName}'s avatar'`} className="profile__image"/>
        <button className="profile__image-overlay" onClick={props.onEditAvatarClick}></button>
      </div>
      <div className="profile__container profile__overflow-element">
        <h1 className="profile__name profile__overflow-element">{userName}</h1>
        <button type="button" className="profile__button profile__button_type_edit" onClick={props.onEditProfileClick} aria-label="Edit profile"></button>
      </div>
      <p className="profile__info profile__overflow-element">{userDescription}</p>
      <button type="button" className="profile__button profile__button_type_add-image" onClick={props.onAddPlaceClick} aria-label="Add image"></button>
    </section>
    <section className="images-container">
      {
        cards.map((card) => {
          <div class="image-card">
          <div class="image-card__image"></div>
          <button type="button" class="image-card__remove-button" aria-label="remove an image"></button>
          <div class="image-card__name-container">
            <h2 class="image-card__name"></h2>
            <div class="image-card__love-button-container">
              <button type="button" class="image-card__love-button" aria-label="Like an image"></button>
              <p class="image-card__love-count">0</p>
            </div>
          </div>
        </div>
        })
      }
    </section>
    </main>
  );
}

export default Main;
