import React from "react";
import {CurrentUserContext} from '../contexts/CurrentUserContext';
import PopupWithForm from './PopupWithForm';


function EditAvatarPopup({isOpen, onClose, onUpdateAvatar}) {
  const inputRef = React.useRef();

  const currentUser = React.useContext(CurrentUserContext);

  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.description);
  }, [currentUser]); 

  function handleSubmit(e) {
    e.preventDefault();
    onUpdateAvatar({
      avatar: ''
    });
    onClose();
  }

  return (
    <PopupWithForm isOpen={isOpen}  onClose={onClose} onSubmit={handleSubmit} name="edit-profile-image" title="Change profile picture">
          <input type="url" ref={inputRef} name="link" className="popup__input popup__input_type_profile-img-link" id="profile-img-link-input" required/>
          <span className="popup__error profile-img-link-input-error"></span>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
